import React from 'react';
import { useState } from 'react';
import LoaderHeading from './util/LoaderHeading';
import ExamCard from './util/ExamCard';
import PageNavigateButton from './util/PageNavigateButton';

function ViewExams() {

    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [demandPage, setDemandPage] = useState(1);

    const [fetchedData, setFetchedData] = useState([]);
    const [pages, setPages] = useState([]);

    async function getData(url) {
        const response = await fetch(url, {
            method: 'GET'
        });
        return response.json(); 
    }



    const loadExams = () => {
        console.log('Demand Page URL : ', demandPage);
        let URL = 'http://localhost:8080/UploadToS3/admins?page=';
        URL += demandPage;
        URL += '&limit=';
        let entityPerPage = localStorage.getItem('perPageExams');
        if(!entityPerPage) {
            localStorage.setItem('perPageExams', 2);
            entityPerPage = 2;
        }
        else entityPerPage = parseInt(entityPerPage);
        URL += entityPerPage;
        getData(URL)
        .then(data => {
            console.log(data);
            setFetchedData(data.data);
            setTotalPages(Math.ceil(data.count/entityPerPage));
            console.log('total pages: ', totalPages);
            // let pagesButton = [];
            // for(let i=0; i<totalPages; i++) {
            //     pagesButton.push(i+1);
            // }
            // setPages(pagesButton);
        });
    }

    const nextPage = () => {
        let dp = currentPage;
        dp = dp < totalPages ? dp + 1 : dp;
        setDemandPage(dp);
        setCurrentPage(dp);
        loadExams();
    }
    const prevPage = () => {
        let dp = currentPage;
        dp = dp > 1 ? dp - 1 : dp;
        setDemandPage(dp);
        setCurrentPage(dp);
        loadExams();
    }
    const pageNumberNavigate = () => {
        let pageElements = document.getElementsByName('pageNumber');
        if(pageElements) {
            for(let i=0; i<pageElements.length; i++) {
                if(pageElements[i].checked) {
                    currentPage = pageElements[i].value;
                    demandPage = currentPage;
                    break;
                }
            }
        }
        loadExams();
    }
    const updateNavigationButtonsState = () => {
        let btn = document.getElementById('prev-btn');
    }

    return (
    <>
        <LoaderHeading 
            description='Exams'
        />
        <div className='content-loaded'>
            <div>
                <div>
                    {

                        fetchedData.map((d, key) => {
                            return <ExamCard 
                                key={key}
                                firstName={d.firstName}
                                lastName={d.lastName}
                                email={d.email}
                                contact={d.contact}
                            />
                        })
                    }
                </div>
                <div className='exam-page-navigation-btns-padding'></div>
                <div className='flex-row jc-sb exam-page-navigation-btns'>
                    <button id="prev-btn" className='btn btn-dark btn-small' onClick={prevPage} disabled>Previous</button>
                    <button id="next-btn" className='btn btn-primary btn-small' onClick={nextPage}>Next</button>
                </div>
                {/* <div className='page-navigate-btn-container'>
                    {
                        pages.map((d, key) => {
                            return <PageNavigateButton
                                key={key}
                                number={d}
                                onclick={loadExams}
                            />
                        })
                    }
                </div> */}

            </div>
        </div>
    </>
    );
}

export default ViewExams;
