import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Request from './components/services/Request';
import Flash from './components/services/Flash';
import logo from './logo.svg';
import './App.css';

import Header from './components/includes/Header';
import Navbar from './components/includes/Navbar';
import Dashboard from './components/Dashboard';
import Footer from './components/includes/Footer';
import Contact from './components/Contact';
import CreateExam from './components/CreateExam';
import ViewExams from './components/ViewExams';
import Settings from './components/Settings';
import Signin from './components/Signin';

function App() {

  const[login, setLogin] = useState(false);
  const[admin, setAdmin] = useState({});

  useEffect(() => {
    let url = 'http://localhost:8080/QuizWit/Login';
    Request.post(url, '')
    .then((res) => {
        console.log(res)
        if(res.success) {
            setAdmin(res.details);
            setLogin(true);
            Flash.message(res.success, 'bg-success');
        }
        else {
            Flash.message(res.error, 'bg-danger');
        }
    })
  }, [])

  return (
    <div className='fix-wrapper'>
      {
        login &&
        <Router>
          <Header 
            setLogin={setLogin}
            admin={admin}
          />
          <div className='body-wrapper'>
            <Navbar />
            <div className='main-wrapper'>
              <div className='loader'>
                <Routes>
                  <Route path='/' element={<Dashboard />}/>
                  <Route path='/create-exam' element={<CreateExam />}/>
                  <Route path='/view-exams' element={<ViewExams />}/>
                  <Route path='/settings' element={<Settings />}/>
                  <Route path='/contact-us' element={<Contact />}/>
                </Routes>
              </div>
            </div>
          </div>
          <Footer />
        </Router>
      }
      {
        !login &&
        <Signin 
          setLogin={setLogin}
          setAdmin={setAdmin}
        />
      }
    </div>
  );
}

export default App;
