import React from 'react';
import '../css/Footer.css'

function Footer() {
  const toggleStatus = () => {
    let navControl = localStorage.getItem('toggleNavbarStatus');
    if(navControl === undefined) {
        localStorage.setItem('toggleNavbarStatus', false);
    }
    navControl = navControl === 'true' ? 'false' : 'true';
    localStorage.setItem('toggleNavbarStatus', navControl);
  }

  const toggleNavbar = () => {
      let links = document.getElementsByClassName('navbar')[0]
      .getElementsByClassName('links')[0]
      .getElementsByTagName('a');
      let navControl = localStorage.getItem('toggleNavbarStatus');
      if(navControl === undefined) {
          localStorage.setItem('toggleNavbarStatus', false);
      }

      for(let i=0; i<links.length; i++) {
          let el = links[i].getElementsByTagName('div')[1];
          let val = navControl === 'true' ? 'block' : 'none';
          let width = navControl === 'true' ? '200px' : '0px';
          el.style.display = val;
          el.style.width = width;
      }
  }
  window.onload = toggleNavbar
  return (
    <>
      <div className='main-footer-container'>
        <div className='navbar-toggle-btn' onClick={toggleStatus}>
            <i className='fas fa-bars'></i>
        </div>
        <div className='main-footer'>
            &copy; 2022-2022 QuizWit All rights reserved. 
        </div>
      </div>
      {/* {toggleNavbar()} */}
    </>
  );
}

export default Footer;
