import React from 'react';
import { useState } from 'react';
import '../css/Footer.css'

function Footer() {
  const [toggle, setToggle] = useState(1);

  const toggleDivHorizontal = (id, t, openWidth, closeWidth) => {
    let el = document.getElementById(id);
    let width = t ? openWidth : closeWidth;
    el.style.transition = "all 1200ms";
    el.style.width = width + 'px';
  }

  const toggleNavbar = () => {
    setToggle(!toggle);
    toggleDivHorizontal('navbar', toggle, 250, 50);
    let style = document.createElement('style');

    document.head.appendChild(style);
  }

  const linkToggle = () => {
    let links = document.getElementById('navbar-link-container')
                .getElementsByTagName('a');
    for(let i=0; i<links.length; i++) {
        links[i].addEventListener('click', () => {
          console.log('great')
            let className = links[i].style.class;
            if(className == 'navbar-active-link') {
                links[i].style.class = 'navbar-inactive-link';
            }
            else {
                links[i].style.class = 'navbar-active-link';
            }
            console.log(className);
        })
    }
  }

  return (
    <>
      <div className='main-footer-container'>
        <div className='navbar-toggle-btn' onClick={linkToggle}>
            <i className='fas fa-bars'></i>
        </div>
        <div className='main-footer'>
            &copy; 2022-2022 QuizWit All rights reserved. 
        </div>
      </div>
    </>
  );
}

export default Footer;
