import React from 'react';
import '../../css/Sections.css';

class Sections extends React.Component {
    constructor(props) {
        super(props);
    }
    closeDialog() {
        document.getElementById('sections-dialog').style.display = 'none';
        document.getElementById('route-overlay').style.display = 'none';
    }
    render() {
        return (
            <>
                <div id='sections-dialog'>
                    <div>
                        <div className='flex=-col flex-full jc-sb'>
                            <div id='exam-name-section-reference'> {this.props.examTitle}</div>
                            {
                                this.props.sections.length != 0 ? 
                                <>
                                    <h3 className='secondary mb-10'>Sections</h3>
                                    <div className="table-container">
                                        <table>
                                        <thead>
                                            <tr>
                                                <th>S No.</th>
                                                <th>Title</th>
                                                <th>Questions</th>
                                                <th>Question Navigation</th>
                                                <th>Action</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {
                                                this.props.sections.map((d, k) => {
                                                    return (
                                                        <>
                                                            <tr key={k}>
                                                                <td>{d.serialNo}</td>
                                                                <td>{d.title}</td>
                                                                <td>{d.questions}</td>
                                                                <td>
                                                                    <label className="custom-toggle-btn mr-5">
                                                                        <input type="checkbox" defaultChecked={examStatus == 1 ? true : false} value={props.examId} onClick={updateStatus}/>
                                                                        <span>
                                                                            <i className="fas fa-check"></i>
                                                                        </span>
                                                                    </label>
                                                                </td>
                                                            </tr>
                                                        </>
                                                    )
                                                })
                                            }
                                        </tbody>
                                        </table>
                                    </div>
                                </>
                                : ''
                            }
                            {
                                this.props.sections.length == 0 ? <div className='danger'>Exam doesn't containe any section</div> : ''
                            }
                        </div>
                        <div className='flex-row jc-s'>
                            <button className='btn btn-fade btn-small' onClick={this.closeDialog}>Close</button>
                        </div>
                    </div>
                </div>
            </>
        );
    }
}

export default Sections;
