import React from 'react';
import { useState } from 'react';
import AceEditor from "react-ace";
import "ace-builds/src-noconflict/mode-c_cpp";
import "ace-builds/src-noconflict/theme-monokai";
import Request from './services/Request';
import Flash from './services/Flash';
import LoaderHeading from './util/LoaderHeading';

import './css/Contact.css';

function Contact() {
    const [programScript, setProgramScript] = useState('');
    const executeCode = () => {
        let resBlock = document.getElementById('program-response');
        console.log(programScript);
        let url = 'http://localhost:8080/QuizWit/Jdoodle';
        let data = '';
        let script = programScript;
        let langugage = 'cpp17';
        let version = '1';
        script = document.getElementById('textarea-code').value;
        console.log('')
        script = script.replace('"', '\"');
        script = script.replace("\n", "\\n");
        console.log(script);
        data = 'script=' + script;
        data += '&language=' + langugage;
        data += '&version='+ version;
        console.log(data);
        Request.post(url, data)
        .then((res) => {
            resBlock.innerText = res;
            console.log(res);
        })
        .catch((err) => {
            Flash.message('Something went wrong', 'bg-danger');
        })
    }

    const fetchScriptFromEditor = (code, e) => {
        console.log(code);
        setProgramScript(code);
        e.end();
    }
    return (
        <>
            <LoaderHeading 
                description='Contact Us'
            />
            <div className='content-loaded '>
                <div>
                    <div className='btn-palette mb-5 flex-row jc-e'>
                        <button className='btn btn-success btn-small' onClick={executeCode}>Run</button>
                    </div>
                    <div className='editor-container'>
                        <AceEditor
                            placeholder="Write your program here..."
                            mode="c_cpp"
                            theme="monokai"
                            name="editor"
                            onChange={fetchScriptFromEditor}
                            fontSize={16}
                            showPrintMargin={true}
                            showGutter={true}
                            highlightActiveLine={true}
                            value={`
                            `}
                            setOptions={{
                            enableBasicAutocompletion: false,
                            enableLiveAutocompletion: false,
                            enableSnippets: false,
                            showLineNumbers: true,
                            tabSize: 2,
                            }}/>
                    </div>
                    <div id='program-response'></div>
                    <textarea name="" id="textarea-code" cols="30" rows="10">

                    </textarea>
                </div>
            </div>
        </>
    );
}

export default Contact;
