import React from 'react';
import { createReactEditorJS } from 'react-editor-js'
import { useState, useEffect } from 'react';
import LoaderHeading from './util/LoaderHeading';
import { EDITOR_JS_TOOLS } from './util/tool';
import EditorJS from '@editorjs/editorjs';

import './css/QuestionEditor.css';

function QuestionEditor() {
    const [editor, setEditor] = useState();
    useEffect(() => {
        const jsEditor = new EditorJS({
           holder: "editorjs",
           tools: {
               heading: EDITOR_JS_TOOLS.header,
               list: EDITOR_JS_TOOLS.list,
               inlineCode: EDITOR_JS_TOOLS.inlineCode,
               quote: EDITOR_JS_TOOLS,
               marker: EDITOR_JS_TOOLS,
               checklist: EDITOR_JS_TOOLS,
               delimiter: EDITOR_JS_TOOLS,
               inlineCode: EDITOR_JS_TOOLS,
               simpleImage: EDITOR_JS_TOOLS,

           }
        });
        setEditor(jsEditor);

    }, []);
    const clearEditor = () => {
        editor.save().then((outputData) => {
          console.log('Article data: ', outputData)
        }).catch((error) => {
          console.log('Saving failed: ', error)
        });
    }
    return (
    <>
        <LoaderHeading
            description='Question Editor'
        />
        <div className='content-loaded'>
            <div>
                <div className='mb-10'>
                    <button className='btn btn-primary btn-small' onClick={clearEditor}>Clear</button>
                </div>
                <div className='question-editor-container'>
                    <h1>New Note</h1>
                    <div id="editorjs"></div>
                </div>
            </div>
        </div>
    </>
    );
}

export default QuestionEditor;
