import React from 'react';
import NavLink from '../util/NavLink';

import '../css/Navbar.css'

function Navbar() {
    var navControl = false;
    const toggleNavbar = () => {
        let links = document.getElementsByClassName('navbar')[0]
        .getElementsByClassName('links')[0]
        .getElementsByTagName('a');
        let navControl = localStorage.getItem('toggleNavbarStatus');
        if(navControl == undefined) {
            localStorage.setItem('toggleNavbarStatus', false);
        }

        for(let i=0; i<links.length; i++) {
            let el = links[i].getElementsByTagName('div')[1];
            let val = navControl ? 'block' : 'none';
            let width = navControl ? '200px' : '0px';
            el.style.display = val;
            el.style.width = width;

        }
        navControl = !navControl;
        localStorage.setItem('toggleNavbarStatus', navControl);

    }
    return (
        <div className='navbar'>
            <div className='links'>
                <NavLink 
                    description='Dashboard'
                    route='/dashboard'
                    icon='fas fa-tachometer-alt'
                    class='navbar-active-link'
                />
                <NavLink 
                    description='Create Exam'
                    route='/create-exam'
                    icon='fas fa-plus-square'
                    class='navbar-inactive-link'
                />
                <button onClick={toggleNavbar}>T</button>
            </div>
        </div>
    );
}

export default Navbar;
