import React from 'react';
import { useState } from 'react';
import LoaderHeading from './util/LoaderHeading';
import ExamCard from './util/ExamCard';
import PageNavigateButton from './util/PageNavigateButton';

function ViewExams() {

    var currentPage = 1;
    var totalPages = 1;
    var demandPage = 1;

    const [fetchedData, setFetchedData] = useState([]);
    const [pages, setPages] = useState([]);

    async function getData(url) {
        const response = await fetch(url, {
            method: 'GET'
        });
        return response.json(); 
    }



    const loadExams = () => {
        let URL = 'http://localhost:8080/UploadToS3/admins?page=';
        URL += demandPage;
        URL += '&limit=';
        let entityPerPage = localStorage.getItem('perPageExams');
        if(!entityPerPage) {
            localStorage.setItem('perPageExams', 2);
            entityPerPage = 2;
        }
        else entityPerPage = parseInt(entityPerPage);
        URL += entityPerPage;
        console.log(URL);
        getData(URL)
        .then(data => {
            setFetchedData(data.data);
            totalPages = Math.ceil(data.count/entityPerPage);
            let pagesButton = [];
            for(let i=0; i<totalPages; i++) {
                pagesButton.push(i+1);
            }
            setPages(pagesButton);
        });
    }

    const nextPage = () => {
        console.log('Next Page');
        demandPage = currentPage + 1;
        demandPage = demandPage > totalPages ? currentPage : demandPage;
        console.log(demandPage);
        loadExams();
    }
    const prevPage = () => {
        demandPage = currentPage - 1;
        demandPage = demandPage < 1 ? currentPage : demandPage;
        loadExams();
    }
    const pageNumberNavigate = () => {
        let pageElements = document.getElementsByName('pageNumber');
        if(pageElements) {
            for(let i=0; i<pageElements.length; i++) {
                if(pageElements[i].checked) {
                    currentPage = pageElements[i].value;
                    demandPage = currentPage;
                    break;
                }
            }
        }
        loadExams();
    }

    return (
    <>
        <LoaderHeading 
            description='Exams'
        />
        <div className='content-loaded'>
            <div>
                <button onClick={loadExams}>Fetch</button>
                <div>
                    {

                        fetchedData.map((d, key) => {
                            return <ExamCard 
                                key={key}
                                firstName={d.firstName}
                                lastName={d.lastName}
                                email={d.email}
                                contact={d.contact}
                            />
                        })
                    }
                </div>
                <div className='flex-row jc-sb'>
                    <button className='btn btn-dark btn-small' onClick={prevPage}>Previous</button>
                    <button className='btn btn-primary btn-small' onClick={nextPage}>Next</button>
                </div>
                <div className='page-navigate-btn-container'>
                    {
                        pages.map((d, key) => {
                            return <PageNavigateButton
                                key={key}
                                number={d}
                                onclick={loadExams}
                            />
                        })
                    }
                </div>
            </div>
        </div>
    </>
    );
}

export default ViewExams;
